import React, { useState, useEffect } from 'react';
import '../styles.css';


export default function QuizCard(props) {
    function getAnswers() {
        let unshuffledAnswers = [...props.incorrectAnswers, props.correctAnswer];
        props.setAnswers(unshuffledAnswers)
    }
    getAnswers();


    const shuffle = (array) => {
        for (let i = array.length - 1; i > 0; i--) {
          let j = Math.floor(Math.random() * (i + 1)); // random index from 0 to i
          [array[i], array[j]] = [array[j], array[i]];
        }
      }

    useEffect(() => (props.setAnswers(shuffle(prev))), [props.answers]);

    function decodeHtml(html) {
        let txt = document.createElement('textarea');
        txt.innerHTML = html;
        return txt.value;
    }
    //Get index of current question
    function getQuestionIndex() {
        return props.quizState.findIndex(question => question.id === props.id);
    }

    const questionIndex = getQuestionIndex();
    //console.log(questionIndex);

    return (
        <main className='quiz-card'>
            <fieldset>
                <legend>{decodeHtml(props.question)}</legend>

                <input 
                    type="radio"
                    id={`answer-${props.answers[0]}`}
                    name={`answer-${props.id}`}
                    value={props.answers[0]}
                    checked={props.quizState[questionIndex].selectedAnswer === props.answers[0]}
                    onChange={(event)=>props.handleChange(event,props.id)}
                />
                <label htmlFor={`answer-${props.answers[0]}`}>{decodeHtml(props.answers[0])}</label>

                <input 
                    type="radio"
                    id={`answer-${props.answers[1]}`}
                    name={`answer-${props.id}`}
                    value={props.answers[1]}
                    checked={props.quizState[questionIndex].selectedAnswer === props.answers[1]}
                    onChange={(event)=>props.handleChange(event,props.id)}

                />
                <label htmlFor={`answer-${props.answers[1]}`}>{decodeHtml(props.answers[1])}</label>

                <input 
                    type="radio"
                    id={`answer-${props.answers[2]}`}
                    name={`answer-${props.id}`}
                    value={props.answers[2]}
                    checked={props.quizState[questionIndex].selectedAnswer === props.answers[2]}
                    onChange={(event)=>props.handleChange(event,props.id)}
                />
                <label htmlFor={`answer-${props.answers[2]}`}>{decodeHtml(props.answers[2])}</label>

                <input 
                    type="radio"
                    id={`answer-${props.answers[3]}`}
                    name={`answer-${props.id}`}
                    value={props.answers[3]}
                    checked={props.quizState[questionIndex].selectedAnswer === props.answers[3]}
                    onChange={(event)=>props.handleChange(event,props.id)}
                />
                <label htmlFor={`answer-${props.answers[3]}`}>{decodeHtml(props.answers[3])}</label>

            </fieldset>
            <hr />
        </main>
    )
}
