import decodeHtml from '../decodeHtml';

export default function Answer({ answer, id }) {
    return (
        <>
           <input 
                type="radio"
                id={`answer-${answer}`}
                name={`answer-${id}`}
                value={answer}
                checked={false}
                />
            <label htmlFor={`answer-${answer}`}>
                {decodeHtml(answer)}
            </label>
        </>
    )
}

